import * as React from 'react';
import { Doughnut } from 'react-chartjs-2';

interface Data {
  dateOfReport: string;
  sex: string;
  ageGroup: string;
  dhb: string;
  internationalTravel: string;
  lastCountryBeforeReturn: string;
  flightNumber: string;
  arrivalDate: string;
}
interface Props {
  totalData: Array<Data>;
}

const Sex: React.FunctionComponent<Props> = ({ totalData }) => {
  const numberOfMales = totalData.filter((a) => a.sex === 'Male').length;
  const numberOfFemales = totalData.filter((a) => a.sex === 'Female').length;

  const sexStats = [numberOfMales, numberOfFemales];

  console.log(stats[0]);
  // For a pie chart
  const data = {
    datasets: [
      {
        data: [stats[0], stats[1]],
        backgroundColor: ['#36A2EB', '#FF6384'],
      },
    ],

    // These labels appear in the legend and in the tooltips when hovering different arcs
    labels: ['Male', 'Female'],
  };

  return <Doughnut data={data}></Doughnut>;
};

export default Sex;
