import * as React from 'react';
import { TotalData } from './Sex';
import { Line } from 'react-chartjs-2';

export const TotalCases: React.FunctionComponent<TotalData> = ({ totalData }) => {
  const dates = Array.from(new Set(totalData.map(item => item.dateOfReport)));
  const dailyRates: { date: string | undefined; number: number }[] = [];

  dates.forEach(date => {
    const dailyRate = totalData.filter(item => item.dateOfReport === date);
    dailyRates.push({
      date: date,
      number: dailyRate.length,
    });
  });

  const Additions: number[] = [];
  console.log(
    dailyRates.reverse().forEach((day, index) => {
      if (index === 0) {
        Additions.push(day.number);
      }
      const newValue = Additions[index - 1] + day.number;
      Additions[index] = newValue;
    }),
  );
  return <Line data={totalData}></Line>;
};
